const ProductItem = ({ name, price, image, navigation }) => {
  const handleProductPress = (product) => {
    const productDetail = {
      name,
      price,
      image,
    };
    navigation.navigate("ProductDetails", { productDetail }); // Pass the selected product to ProductDetails
  };

  return (
    <TouchableOpacity onPress={(item) => handleProductPress(item)}>
      <View style={styles.productItem}>
        <Image source={{ uri: image }} style={styles.productImage} />
        <View style={styles.productDetails}>
          <Text style={styles.productName}>{name}</Text>
          <Text style={styles.productPrice}>{price}</Text>
        </View>
      </View>
    </TouchableOpacity>
  );
};

const Products = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Products List</Text>
      <FlatList
        data={products}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <ProductItem
            name={item.name}
            price={item.price}
            image={item.image}
            navigation={navigation}
          />
        )}
      />
    </View>
  );
};

